mapper.select:
select count(*) as count from member
 
	
		withdrawaldate is not null
		authority_code=1
		authority_code=1 and withdrawaldate>=#{today}
		authority_code in (2,3)
		authority_code in (2,3) and withdrawaldate>=#{today}	
		authority_code=1 and regdate>=#{today}
		authority_code in (2,3) and regdate>=#{today}	
		withdrawaldate >= #{today} and authority_code=1	
		withdrawaldate >= #{today} and authority_code in(2,3)
