mapper.select:
select *
	from
	(
	    select A.*, rownum as RNUM 
	    from
	    (
		select * from apply where member_code=#{memberCode}
		order by applydate desc
		)A
	) 
	
	 RNUM>#{firstRecordIndex}  and
	RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

select *
	from
	(
	    select A.*, rownum as RNUM 
	    from
	    (
		select * from apply where jobopening in 
		
		
			#{jobopening}
		
		order by applydate desc
		)A
	) 
	
	 RNUM>#{firstRecordIndex}  and
	RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
